class Solution:
    def minimumTotal(self, triangle:list[list[int]]) -> int:
        # inplace
        # row idx + 1 == row length
        n = len(triangle)

        # bottom up starting from last second row
        for row_idx in range(n-2,-1,-1):
            for cell in range(row_idx+1):
                triangle[row_idx][cell] += min(
                    triangle[row_idx+1][cell],
                    triangle[row_idx+1][cell+1]
                )

        return triangle[0][0]

